import React from "react";
import { useForm } from "react-hook-form";
import s from "./index.module.css";
import { Context } from "../../contex";
import { useContext } from "react";

export default function AddToDos() {
  const { createNewUser } = useContext(Context);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ mode: "onBlur" });

  const submit = data => {
    console.log(data);
    addToDos(data, createNewUser)
  };

  const todoRegister = register("todo", {
    required: "*Field todo is required",
  });

  

  return (
    <div>
      <form className={s.add_todo_form} onSubmit={handleSubmit(submit)}>
        <input
          type="text"
          name="todo"
          placeholder="todo"
          {...todoRegister}
        />


        <button>Add user</button>
      </form>

      <div>
        <div>{errors?.firstname && <p>{errors?.firstname?.message}</p>}</div>

       
      </div>
    </div>
  );
}
